import React from "react";
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }

  // ComponentDidMount is used to
  // execute the code 
  componentDidMount() {
    fetch(
      "https://api.bigbox.online/api/v1/users/featured_stores?lat=13.0827&long=80.2707")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true
        });
      })
  }

  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;
    return (
      <div className="App">
        {
          items.stores.map((item) => (
            <div key={item.id} className="userdetail" >
              <h1>{item.name}</h1>
              <img src={item.cover_pic_url} alt="" />
            </div>

          ))
        }
      </div>
    );
  }
}

export default App;
