import React from "react";
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }

  // ComponentDidMount is used to
  // execute the code 
  componentDidMount() {
    fetch(
      "https://api.bigbox.online/api/v1/users/featured_stores?lat=13.0827&long=80.2707")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true
        });
      })
  }

  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> .... </h1> </div>;
    return (
      <div className="App">
        <div className="home-body">
          {
            items.stores.map((item) => (
              <div className="home-body">
                <div className="home-cat">Categories</div>
                <div key={item.id} className="home-store" >

                  <img src={item.cover_pic_url} alt="" />
                  <h1>{item.name}</h1>
                </div>
              </div>
            ))
          }
        </div>
      </div>
    );
  }
}

export default App;
