import React from "react";
import logo from './logo.svg';
import './App.css';



class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }

  // ComponentDidMount is used to
  // execute the code 
  componentDidMount() {
    fetch(
      "https://api.bigbox.online/api/v1/users/featured_stores?lat=13.0827&long=80.2707")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true
        });
      })
  }



  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> .... </h1> </div>;

    return (
      <div className="bbhome">
        <div style={{ paddingLeft: "30px" }}>
          <h1 style={{ fontSize: "80px", marginBottom: "5px" }}>video call your favourite brands</h1>
          <h5 style={{ fontSize: "30px", marginTop: "0", fontWeight: 400 }}>discover, follow and shop from your favourite stores. anytime. anywhere.</h5>
        </div>
        <div className="App">
          <div className="sidebar">
            <div style={{ fontSize: "20px", fontWeight: 600 }}>categories</div>
          </div>
          <div className="home-body">
            {
              items.stores.map((item) => (
                <div key={item.id} className="home-store" >
                  <img src={item.brand_logo_url} alt="" className="brand-logo" />
                  <a style={{ textDecoration: 'none' }} href={`https://in.bigbox.online/${item.slug}`} target="_blank" >

                    <img className="home-storeImg" src={item.cover_pic_mobile_url} alt="" />
                    <h1>{item.brand.name}</h1>
                  </a>
                </div>
              ))
            }
          </div>
        </div>
      </div>
    );
  }
}

export default App;
