import React, { useState, useEffect } from "react";
import logo from './logo.svg';
import './App.css';
import axios from "axios";






const App = () => {
  const [category, setCategory] = useState([]);
  const [store, setStore] = useState([]);

  const fetchData = () => {
    const categoryApi = "https://api.bigbox.online/api/v1/users/brand_categories?page=1&limit=10";
    const storeApi = "https://api.bigbox.online/api/v1/users/featured_stores?lat=13.0827&long=80.2707";

    const getCategory = axios.get(categoryApi);
    const getStore = axios.get(storeApi);
    axios.all([getCategory, getStore]).then(
      axios.spread((...allData) => {
        const allCategory = allData[0].brand_categories.name;
        const allStore = allData[1];
        console.log(allCategory);
        console.log(allStore)
        setCategory(allCategory);
        setStore(allStore);
      })
    )
  }

  useEffect(() => {
    fetchData()
  }, [])

  return (
    <div>
      Category Name: {category}
      {/* Store Nmae: {store} */}
    </div>
  );
}

export default App;



















