import React from "react";
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }

  // ComponentDidMount is used to
  // execute the code 
  componentDidMount() {
    fetch(
      "https://api.uat.bigbox.online/api/v1/store_admins/stores/:1/tags")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true
        });
      })
  }

  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;
    console.log(items);
    return (
      <div className="App">
        <h1> Fetch data from an api in react </h1>  {
          items.stores.map((item) => (

            <div className="userdetail" key={item.id}>
              <h1>{item.name}</h1>
            </div>

          ))
        }
      </div>
    );
  }
}

export default App;
